mapper.update:
UPDATE contents
SET title=#{title}, content=#{content:VARCHAR}, 
      thumbs=#{thumbs}, files=#{files}, sizes=#{sizes}, word=#{word}
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND word LIKE '%' || #{word} || '%'

SELECT contentsno, categoryno, title, content, good, thumbs, files, sizes, cnt, replycnt, rdate, grpno, indent, ansnum, word, r FROM ( SELECT contentsno, categoryno, title, content, good, thumbs, files, sizes, cnt, replycnt, rdate, grpno, indent, ansnum, word, rownum as r FROM ( SELECT contentsno, categoryno, title, content, good, thumbs, files, sizes, cnt, replycnt, rdate, grpno, indent, ansnum, word FROM contents
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND word LIKE '%' || #{word} || '%'
ORDER BY grpno DESC, ansnum ASC ) )
WHERE r >= #{startNum} AND r <= #{endNum}

mapper.update:
UPDATE contents
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
